import React, { useEffect} from 'react'
import { useSelector} from "react-redux"
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import { baseUrl } from '../..';
import './Basket.css'

export default function Basket() {
    let basket = useSelector(store=>store.basket.basket)
    let dispatch = useDispatch()
    useEffect(() => {
      localStorage.setItem("basket", JSON.stringify(basket));
    });

  return (
    <div>
      <Header/>
      <div className='basket_main'>
        <div>
         <h2 className='basket_title'>Basket</h2>
      </div>
      <div>
              {basket.map( elem =>  
              <div>
                <hr/>
                <div className="basket_wrapper" >
                   <img
                src={baseUrl + elem.image}
                alt="photo"
                width="190"
                height="190"
                className='basket_img'
              />
              <div>
                <p> {elem.title} </p>
                <button onClick={() => dispatch(remCountAction(elem.id))}>-</button>
                    <p>{elem.count}</p>
                <button onClick={() => dispatch(addCountAction(elem.id))} >+</button>

              </div>

              <p className="discont_price">{elem.discont_price}$</p>
              <p className="price">{elem.price}$</p> 
              </div>



               </div>
                )}
            </div>
      </div>

        <Footer/>
    </div>
  )
}
