const defaultState = {
    priceSum:[]
}



export const sumReducer = (state = defaultState, action) => {
    switch (action.type) {
        case 'SUM_PRICE':
            let amount = 0;

            return {...state, priceSum: state.priceSum.forEach((item)=>{
                amount += item.amount

            })}
        default:
            return state
    } 
}

export const AddSumPriceAction = (payload) => ({type:'SUM_PRICE' , payload})
