import { baseUrl } from "..";
import {
  AddCategoriesAction,
  AddOneCategoryAction,
} from "../store/categoriesReducer";

export const fetchCategory = () => {
  return function (dispatch) {
    fetch(baseUrl + "/categories/all")
      .then((res) => res.json())
      .then((data) => dispatch(AddCategoriesAction(data)));
  };
};

export const fetchCategory2 = (id) => {
  return function (dispatch) {
    let url = baseUrl + `/categories/${id}`;
    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        dispatch(AddOneCategoryAction(data));
      });
  };
};
