let defaultState = {
    basket: []
}

const ADD_PRODUCT = 'ADD_PRODUCT'



export const basketReducer = (state = defaultState, action) => {
    switch (action.type){
        case ADD_PRODUCT:
            return {...state, basket:[...state.basket,{...action.payload, count:0}] }


        default: 
            return state
    }
}


export const addProductAction = (payload) => ({type: ADD_PRODUCT, payload})
