    <div>
      <h1>Корзина</h1>

            <div>

                {basket.map( elem => 
                <div className="basket_wrapper" >
                    <p>{elem.name}</p>
                    <button onClick={() => dispatch(remProductAction(elem.id))}>-</button>
                    <p>{elem.count}</p>
                    <button onClick={() => dispatch(addProductAction(elem.id))} >+</button>
                </div>
                )}
            </div>
    </div>
  )
}
