const defaultState = () => {
    return function(dispatch){
        let url = 'http://localhost:3333/categories/all'
        fetch(url)
            .then(res=>res.json())
            .then(data=>dispatch(AddCategoryAction(data)))
    }
}

export const categoryReducer = (state = defaultState,action) => {
    switch (action.type){
        case 'ADD_CATEGORY':
            return {...state,elem:[...state.elem,...action.payload]}
        default:
            return state
    }
}

export const AddCategoryAction = (payload)=>({type:'ADD_CATEGORY',payload})
