import React from 'react'
import { useSelector, useDispatch } from "react-redux"

export default function Basket() {
    let basket = useSelector(store=>store.basket.basket)
    let dispatch = useDispatch()
  return (
    <div>
      <h1>Корзина</h1>
            <div>
                {basket.map( elem => 
                <div className="goods_wrapper" >
                    <p>{elem.name}</p>
                    <button onClick={() => dispatch(remCountAction(elem.id))}>-</button>
                    <p>{elem.count}</p>
                    <button onClick={() => dispatch(addCountAction(elem.id))} >+</button>
                    <button>X</button>
                </div>
                )}
                <button onClick={() => dispatch(addGoodsAction(prompt()))}>Добавить новый товар</button>
            </div>
    </div>
  )
}
