import React from "react";
import Header from "../Header/Header";
import mainImg from "../img/image2.svg";
import { useSelector, useDispatch } from "react-redux";
import "./Main.css";
import { fetchCategories } from "../category/Category";

function Main() {
  let categories = useSelector((store) => store.categories.categories);
  let dispatch = useDispatch();

  return (
    <div>
      <Header />
      <div className="section_one">
        <div className="section_one_wrapper">
          <h1>New year sale</h1>
          <div>
            <button className="btn btn_sale">all sales</button>
            <button className="btn btn_more">more</button>
          </div>
        </div>
        <div>
          <img src={mainImg} width="900" height="600" />
        </div>
      </div>
      <div className="section_two">
        <div className="section_two_wrapper">
          <h3>Categories</h3>
          <button className="btn_category">All categories</button>
        </div>
        <div>
          <button onClick={() => dispatch(fetchCategories())}>
            Добавить список сотрудников 
          </button>
        </div>
        {categories.map((elem) => 
                <div 
                    key={index} 
                    // onDoubleClick={() => dispatch(remEmplByIdAction(elem.id))
                    onDoubleClick={() => dispatch(remEmplByIdAction(index))}
                >
                    <h3>{elem.name}</h3>
                    <p>{elem.id}</p>
                </div>
            )}
      </div>
    </div>
  );
}

export default Main;
