import { baseUrl } from "..";
import {
  AddCategoriesAction,
} from "../store/categoriesReducer";


export const fetchCategory = (id) => {
  return function (dispatch) {
    let url = baseUrl + `/categories/${id}`;
    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        dispatch(AddOneCategoryAction(data));
      });
  };
};
