export const categoryReducer = (state, action) => {
    switch (action.type) {
        case 'ADD_CATEGORY':
            return {...state, empl: [...state.empl, ...action.payload]}
        default:
            return state
    } 
}

export const AddCategoryAction = (payload) => ({type:'ADD_CATEGORY' , payload})
