const defaultState = {
    priceSum:[]
}



export const sum = (state = defaultState, action) => {
    switch (action.type) {
        case 'SUM_PRICE':
            return {...state, priceSum: [...action.payload.discont_price]}
        default:
            return state
    } 
}

export const AddSumPriceAction = (payload) => ({type:'SUM_PRICE' , payload})
