import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchCategory } from "../../asyncAction/categories";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import { Link } from "react-router-dom";
import { baseUrl } from "../..";

function Category() {
  let categories = useSelector((store) => store.categoriesList.categoriesList);
  let dispatch = useDispatch();
  useEffect(() => {
    dispatch(fetchCategory());
  }, []);

  return (
    <div className="category_part">
      <div className="category_part_wrapper">
        <Header />
        <div className="all_categories">
          <div>
            <h3>Categories</h3>
          </div>
          <div className="category_types">
            {categories.map((elem) => (
              <div>
                <Link key={elem.id} to={`/categories/${elem.id}`}>
                  <img
                    src={baseUrl + elem.image}
                    alt="photo"
                    width="318"
                    height="330"
                  />
                  <p className="category_text">{elem.title}</p>
                </Link>
              </div>
            ))}
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default Category;
