import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { fetchCategory2 } from '../../asyncAction/category';

export default function CategoryTypes() {
    let categories = useSelector((store) => store.categories.categories);
    let dispatch = useDispatch();
    useEffect(() => {
        dispatch(fetchCategory2());
      }, []);
  return (
    <div>
       <div className='category_part'>
        <div className='category_part_wrapper'>
            <Header/>
      <div className='all_categories'>
        <div>
          <h3>Categories</h3>
        </div>
        <div className="category_types">
          {categories.map((elem) => (
            <div>
              <img
                src={`http://localhost:3333${elem.image}`}
                alt="photo"
                width="318"
                height="330"
              />
              <Link onClick={()=>dispatch(fetchCategory2(elem.id))} to='/categoryTypes'>{elem.title}</Link>
            </div>
          ))}
        </div>
      </div>
        </div>
        <Footer/> 
    </div>
  )
}
    </div>
  )
}
