import { baseUrl } from "..";
import { AddOnProductAction } from "../store/productsReducer";

export const fetchProducts = (id) => {
  return function (dispatch) {
    fetch(baseUrl + ((id !== 'all') ? `/categories/${id}` : `/products/all`))
      .then(res => res.json())
      .then(data => dispatch(AddOneProductAction(data)));
}
}
