import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { fetchCategory } from '../../asyncAction/category';
import Header from '../Header/Header'
import Footer from '../Footer/'


function Category() {
    let categories = useSelector((store) => store.categories.categories);
    let dispatch = useDispatch();
    useEffect(() => {
        dispatch(fetchCategory());
      }, []);
  return (
    <div className='category_part'>
        <div>
            <Header/>
      <div className="section_two" >
        <div className="section_two_wrapper">
          <h3>Categories</h3>
          <button className="btn_category">All categories</button>
        </div>
        <div className="category_types">
          {categories.map((elem) => (
            <div>
              <img
                src={`http://localhost:3333${elem.image}`}
                alt="photo"
                width="318"
                height="330"
              />
              <p className="category_text">{elem.title}</p>
            </div>
          ))}
        </div>
      </div> 
        </div>
     <Footer/>
    </div>
  )
}

export default Category
